% simplex primalfunction [vb, xb, z, r] = simplexP(c, A, b)% Simplex fase Idisplay("Simplex Fase I")%creem les variables artificialsc1 = cat(2, zeros(1, columns(A)), ones(1, rows(A)))' % concatenació de n zeros i m unsA1 = [A, eye(rows(A), rows(A))] % concatenació de A + identidad de mxmvb1 = columns(A)+1 : columns(A) + rows(A) % indexos variables artificialsvn1 = 1 : columns(A)iout = 0;xb1 = bc1(vb1)z1 = c1(vb1)'*xb1niter = 0;while (iout == 0)  niter = niter + 1  [vb1, vn1, xb1, z1, iout] = simplexP_iter(c1, A1, b, vb1, vn1, xb1, z1, iout);  vb1endwhile;z1if(z1 != 0) display("El problema original es infactible."); return; end% Simplex fase IIdisplay("Simplex Fase II")vb = vb1vn = [1:columns(A)]vn(vb) = []xb = A(:, vb)^(-1)*bz = c(vb)'*xbiout = 0;niter = 0;while (iout == 0)  niter = niter + 1  [vb, vn, xb, z, iout] = simplexP_iter(c, A, b, vb, vn, xb, z, iout);endwhile;endfunction;